# read avg, max, min from a file containing only integers
# run on txt files generated by ./scripts/benchmark_full_vs_default_snaps/process_latencies.py
# script via the os commands in the main function
def get_stats(filename):
    f = open(filename, 'r')
    lines = f.readlines()
    f.close()
    lines = [int(line) for line in lines]
    return (sum(lines)/len(lines), max(lines), min(lines))


for filename in ['vhive_log_helloworld.txt', 'vhive_log_pyaes.txt', 'vhive_log_rnn.txt']:
    print(filename)
    print(get_stats(filename))